const DIRS = [[0,1],[0,-1],[1,0],[-1,0]];
const TIMEOUT_MS = 60000;

function solvePuzzle\(puzzleIndex, puzzle\) {
    const { rows, cols, numbers, walls } = puzzle;
    const totalCells = rows * cols;

    // Build wall set for O\(1\) lookup
    const wallSet = new Set\(\);
    for \(const [r1, c1, r2, c2] of walls\) {
        wallSet.add\(`${r1},${c1}-${r2},${c2}`\);
        wallSet.add\(`${r2},${c2}-${r1},${c1}`\);
    }

    // Parse checkpoints: sorted by their number value
    // numbers maps cellIndex -> checkpointNumber
    const checkpointsByNumber = []; // array of { cellIndex, row, col, number }
    for \(const [cellIdx, num] of Object.entries\(numbers\)\) {
        const idx = parseInt\(cellIdx\);
        const r = Math.floor\(idx / cols\);
        const c = idx % cols;
        checkpointsByNumber.push\({ cellIndex: idx, row: r, col: c, number: num }\);
    }
    checkpointsByNumber.sort\(\(a, b\) => a.number - b.number\);

    // Build checkpoint lookup: cellIndex -> index in checkpointsByNumber
    const checkpointIndexMap = new Map\(\);
    for \(let i = 0; i < checkpointsByNumber.length; i++\) {
        checkpointIndexMap.set\(checkpointsByNumber[i].cellIndex, i\);
    }

    const startCell = checkpointsByNumber[0];
    const endCell = checkpointsByNumber[checkpointsByNumber.length - 1];

    // Adjacency check \(no wall between\)
    function canMove\(r1, c1, r2, c2\) {
        if \(r2 < 0 || r2 >= rows || c2 < 0 || c2 >= cols\) return false;
        return !wallSet.has\(`${r1},${c1}-${r2},${c2}`\);
    }

    // Precompute adjacency list for each cell
    const adj = new Array\(totalCells\);
    for \(let r = 0; r < rows; r++\) {
        for \(let c = 0; c < cols; c++\) {
            const idx = r * cols + c;
            adj[idx] = [];
            for \(const [dr, dc] of DIRS\) {
                const nr = r + dr;
                const nc = c + dc;
                if \(canMove\(r, c, nr, nc\)\) {
                    adj[idx].push\(nr * cols + nc\);
                }
            }
        }
    }

    // Manhattan distance between two cell indices
    function manhattan\(a, b\) {
        const ar = Math.floor\(a / cols\), ac = a % cols;
        const br = Math.floor\(b / cols\), bc = b % cols;
        return Math.abs\(ar - br\) + Math.abs\(ac - bc\);
    }

    // Visited array \(faster than Set\)
    const visited = new Uint8Array\(totalCells\);
    const path = new Array\(totalCells\);

    const startTime = Date.now\(\);
    let solved = false;
    let timedOut = false;
    let iterations = 0;

    // DFS with backtracking
    // nextCheckpoint: index into checkpointsByNumber for the NEXT checkpoint we must reach
    function dfs\(cellIdx, step, nextCheckpoint\) {
        if \(timedOut\) return false;

        // Check timeout every 100000 iterations
        iterations++;
        if \(\(iterations & 0xFFFFF\) === 0\) {
