        if \(nextCheckpoint < checkpointsByNumber.length\) {
            const nextCp = checkpointsByNumber[nextCheckpoint];
            const dist = manhattan\(cellIdx, nextCp.cellIndex\);
            if \(dist > remaining\) return false;
        }

        // Also ensure we can reach the final cell
        const distToEnd = manhattan\(cellIdx, endCell.cellIndex\);
        if \(distToEnd > remaining\) return false;

        // Try neighbors
        const neighbors = adj[cellIdx];
        for \(let i = 0; i < neighbors.length; i++\) {
            const next = neighbors[i];
            if \(visited[next]\) continue;

            // Check checkpoint constraints
            let newNextCheckpoint = nextCheckpoint;

            // If 'next' is a checkpoint cell
            if \(checkpointIndexMap.has\(next\)\) {
                const cpIdx = checkpointIndexMap.get\(next\);
                if \(cpIdx === newNextCheckpoint\) {
                    // Good, we're visiting the next checkpoint in order
                    newNextCheckpoint = cpIdx + 1;
                } else if \(cpIdx > newNextCheckpoint\) {
                    // Visiting a future checkpoint out of order - skip
                    continue;
                }
                // cpIdx < nextCheckpoint means already visited checkpoint \(start cell\), that's fine
                // Actually no - if cpIdx < nextCheckpoint, it should already be visited
                // The start cell \(cpIdx=0\) is already accounted for
            }

            visited[next] = 1;
            if \(dfs\(next, step + 1, newNextCheckpoint\)\) return true;
            visited[next] = 0;
        }

        return false;
    }

    // Start DFS from the start cell
    visited[startCell.cellIndex] = 1;
    // The start cell is checkpoint 0, so next checkpoint is 1
    solved = dfs\(startCell.cellIndex, 0, 1\);

    return { solved, timedOut, path: solved ? path.slice\(\) : null, iterations };
}

