// validate_puzzles2.js - Zip Puzzle Validator with DFS + pruning
// Rules: Hamiltonian path on grid, orthogonal moves, no wall crossings,
//        visit numbered checkpoints in ascending order.

const PUZZLES = [
    // Day 0 Monday 5x5 - ALREADY WORKS
    { rows: 5, cols: 5, numbers: { 0: 1, 4: 2, 24: 3, 20: 4 }, walls: [] },
    // Day 1 Tuesday 5x5 - ALREADY WORKS
    { rows: 5, cols: 5, numbers: { 0: 1, 12: 2, 24: 3 }, walls: [[1,2,2,2],[2,3,2,4]] },
    // Day 2 Wednesday 6x6 - FIXED parity
    { rows: 6, cols: 6, numbers: { 0: 1, 5: 2, 16: 3, 34: 4 }, walls: [[1,2,1,3],[3,0,3,1]] },
    // Day 3 Thursday 6x6 - FIXED parity
    { rows: 6, cols: 6, numbers: { 0: 1, 11: 2, 24: 3, 29: 4 }, walls: [[0,2,1,2],[2,4,2,5],[4,1,4,2]] },
    // Day 4 Friday 7x7 - ALREADY WORKS
    { rows: 7, cols: 7, numbers: { 0: 1, 6: 2, 24: 3, 48: 4 }, walls: [[1,3,2,3],[3,1,3,2],[4,5,5,5]] },
    // Day 5 Saturday 7x7 - ALREADY WORKS
    { rows: 7, cols: 7, numbers: { 0: 1, 3: 2, 24: 3, 45: 4, 48: 5 }, walls: [[0,4,1,4],[2,1,2,2],[3,5,4,5],[5,2,5,3]] },
    // Day 6 Sunday 8x8 - FIXED parity
    { rows: 8, cols: 8, numbers: { 0: 1, 7: 2, 27: 3, 36: 4, 55: 5, 62: 6 }, walls: [[1,3,1,4],[2,6,3,6],[4,1,4,2],[5,4,6,4],[6,5,6,6]] }
];

const DAY_NAMES = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];

function getTimeout\(rows, cols\) {
    const total = rows * cols;
    if \(total <= 25\) return 120000;
    if \(total <= 36\) return 120000;
    if \(total <= 49\) return 180000;
    return 240000;
}

function solvePuzzle\(puzzle, timeoutMs\) {
    const { rows, cols, numbers, walls } = puzzle;
    const totalCells = rows * cols;

    // Parity check
    const numEntries = Object.entries\(numbers\).map\(\([idx, num]\) => [parseInt\(idx\), num]\);
    numEntries.sort\(\(a, b\) => a[1] - b[1]\);
    const startCell = numEntries[0][0];
    const endCell = numEntries[numEntries.length - 1][0];
    const startRow = Math.floor\(startCell / cols\);
    const startCol = startCell % cols;
    const endRow = Math.floor\(endCell / cols\);
    const endCol = endCell % cols;
    const startParity = \(startRow + startCol\) % 2;
    const endParity = \(endRow + endCol\) % 2;

    console.log\('  Start cell: ' + startCell + ' \(' + startRow + ',' + startCol + '\) parity=' + startParity\);
    console.log\('  End cell: ' + endCell + ' \(' + endRow + ',' + endCol + '\) parity=' + endParity\);
    console.log\('  Total cells: ' + totalCells + ' \(' + \(totalCells % 2 === 0 ? 'even' : 'odd'\) + '\)'\);

    if \(totalCells % 2 === 0 && startParity === endParity\) {
        console.log\('  PARITY VIOLATION: Even grid requires different parities for start and end!'\);
        return { solved: false, reason: 'PARITY_VIOLATION', path: null };
    }
    if \(totalCells % 2 === 1 && startParity !== endParity\) {
        console.log\('  PARITY VIOLATION: Odd grid requires same parities for start and end!'\);
        return { solved: false, reason: 'PARITY_VIOLATION', path: null };
    }
    console.log\('  Parity check: PASSED'\);

    // Build wall set
    const wallSet = new Set\(\);
    for \(const [r1, c1, r2, c2] of walls\) {
        const a = r1 * cols + c1;
        const b = r2 * cols + c2;
        wallSet.add\(a + '-' + b\);
        wallSet.add\(b + '-' + a\);
    }

    // Build adjacency list
    const adj = new Array\(totalCells\);
    for \(let i = 0; i < totalCells; i++\) {
        adj[i] = [];
        const r = Math.floor\(i / cols\);
        const c = i % cols;
        if \(r > 0\) {
            const j = \(r - 1\) * cols + c;
            if \(!wallSet.has\(i + '-' + j\)\) adj[i].push\(j\);
        }
        if \(r < rows - 1\) {
            const j = \(r + 1\) * cols + c;
            if \(!wallSet.has\(i + '-' + j\)\) adj[i].push\(j\);
        }
        if \(c > 0\) {
            const j = r * cols + \(c - 1\);
            if \(!wallSet.has\(i + '-' + j\)\) adj[i].push\(j\);
        }
        if \(c < cols - 1\) {
            const j = r * cols + \(c + 1\);
            if \(!wallSet.has\(i + '-' + j\)\) adj[i].push\(j\);
        }
    }

    // Build checkpoint sequence
    const checkpoints = numEntries.map\(function\(e\) { return e[0]; }\);
    const checkpointOrder = new Map\(\);
    for \(let i = 0; i < checkpoints.length; i++\) {
        checkpointOrder.set\(checkpoints[i], i\);
    }

    // DFS state
    const visited = new Uint8Array\(totalCells\);
    const path = new Array\(totalCells\);
    let nodesExplored = 0;
    const startTime = Date.now\(\);
    let timedOut = false;
    let solutionPath = null;

    // BFS connectivity check
    const bfsQueue = new Int32Array\(totalCells\);
    const bfsSeen = new Uint8Array\(totalCells\);

    function isConnected\(currentCell, visitedCount\) {
        const remaining = totalCells - visitedCount;
        if \(remaining <= 2\) return true;

        bfsSeen.fill\(0\);
        let head = 0, tail = 0;
        bfsQueue[tail++] = currentCell;
        bfsSeen[currentCell] = 1;
        let count = 1;

        while \(head < tail\) {
            const node = bfsQueue[head++];
            const neighbors = adj[node];
            for \(let ni = 0; ni < neighbors.length; ni++\) {
                const nb = neighbors[ni];
                if \(!bfsSeen[nb] && !visited[nb]\) {
                    bfsSeen[nb] = 1;
                    count++;
                    bfsQueue[tail++] = nb;
                }
            }
        }
        return \(count - 1\) >= remaining;
    }

    // Isolated cell detection
    function hasIsolatedCell\(currentCell\) {
        for \(let i = 0; i < totalCells; i++\) {
            if \(visited[i] || i === currentCell\) continue;
            let hasNeighbor = false;
            const neighbors = adj[i];
            for \(let ni = 0; ni < neighbors.length; ni++\) {
                const nb = neighbors[ni];
                if \(!visited[nb] || nb === currentCell\) {
                    hasNeighbor = true;
                    break;
                }
            }
            if \(!hasNeighbor\) {
                let adjToCurrent = false;
                for \(let ni = 0; ni < neighbors.length; ni++\) {
                    if \(neighbors[ni] === currentCell\) { adjToCurrent = true; break; }
                }
                if \(!adjToCurrent\) return true;
            }
        }
        return false;
    }

    // DFS with backtracking
    function dfs\(cell, step, nextCheckpointIdx\) {
        if \(\(nodesExplored & 0xFFFF\) === 0\) {
            if \(Date.now\(\) - startTime > timeoutMs\) {
                timedOut = true;
                return false;
            }
        }
        nodesExplored++;

        path[step] = cell;
        visited[cell] = 1;

        // Check if this cell is a checkpoint
        let updatedCpIdx = nextCheckpointIdx;
        if \(checkpointOrder.has\(cell\)\) {
            const cpIdx = checkpointOrder.get\(cell\);
            if \(cpIdx === nextCheckpointIdx\) {
                updatedCpIdx = nextCheckpointIdx + 1;
            } else if \(cpIdx > nextCheckpointIdx\) {
                visited[cell] = 0;
                return false;
            }
        }

        // Done?
        if \(step === totalCells - 1\) {
            if \(updatedCpIdx === checkpoints.length\) {
                solutionPath = path.slice\(0, totalCells\);
                visited[cell] = 0;
                return true;
            }
            visited[cell] = 0;
            return false;
        }

        const remaining = totalCells - step - 1;

        // Isolated cell detection
        if \(remaining <= 20 || \(remaining > 20 && \(nodesExplored & 0x3FF\) === 0\)\) {
            if \(hasIsolatedCell\(cell\)\) {
                visited[cell] = 0;
                return false;
            }
        }

        // Connectivity check
        if \(remaining > 3 && \(step < 6 || \(nodesExplored & 0x1FFF\) === 0 || remaining <= 15\)\) {
            if \(!isConnected\(cell, step + 1\)\) {
                visited[cell] = 0;
                return false;
            }
        }

        // Manhattan distance pruning to next checkpoint
        if \(updatedCpIdx < checkpoints.length\) {
            const target = checkpoints[updatedCpIdx];
            const tr = Math.floor\(target / cols\);
            const tc = target % cols;
            const cr = Math.floor\(cell / cols\);
            const cc = cell % cols;
            const dist = Math.abs\(tr - cr\) + Math.abs\(tc - cc\);
            if \(dist > remaining\) {
                visited[cell] = 0;
                return false;
            }
        }

        // Warnsdorff heuristic: sort neighbors by degree ascending
        const neighborInfo = [];
        const cellNeighbors = adj[cell];
        for \(let ni = 0; ni < cellNeighbors.length; ni++\) {
            const nb = cellNeighbors[ni];
            if \(!visited[nb]\) {
                let degree = 0;
                const nbNeighbors = adj[nb];
                for \(let nni = 0; nni < nbNeighbors.length; nni++\) {
                    if \(!visited[nbNeighbors[nni]] && nbNeighbors[nni] !== cell\) degree++;
                }
                neighborInfo.push\([nb, degree]\);
            }
        }
        neighborInfo.sort\(function\(a, b\) { return a[1] - b[1]; }\);

        for \(let ni = 0; ni < neighborInfo.length; ni++\) {
            if \(timedOut\) break;
            if \(dfs\(neighborInfo[ni][0], step + 1, updatedCpIdx\)\) {
                visited[cell] = 0;
                return true;
            }
        }

        visited[cell] = 0;
        return false;
    }

    console.log\('  Starting DFS from cell ' + startCell + '...'\);
    const found = dfs\(startCell, 0, 1\);

    const elapsed = \(\(Date.now\(\) - startTime\) / 1000\).toFixed\(2\);
    console.log\('  Nodes explored: ' + nodesExplored.toLocaleString\(\)\);
    console.log\('  Time: ' + elapsed + 's'\);

    if \(timedOut\) {
        return { solved: false, reason: 'TIMEOUT', path: null, nodesExplored: nodesExplored, elapsed: elapsed };
    }
    if \(found\) {
        return { solved: true, reason: null, path: solutionPath, nodesExplored: nodesExplored, elapsed: elapsed };
    }
    return { solved: false, reason: 'NO_SOLUTION', path: null, nodesExplored: nodesExplored, elapsed: elapsed };
}

// Main
console.log\('=== ZIP Puzzle Validator ===\\n'\);

var results = [];

for \(var i = 0; i < PUZZLES.length; i++\) {
    var puzzle = PUZZLES[i];
    var dayName = DAY_NAMES[i] || \('Day ' + i\);
    var timeoutMs = getTimeout\(puzzle.rows, puzzle.cols\);

    console.log\('--- Puzzle ' + i + ' \(' + dayName + '\) - ' + puzzle.rows + 'x' + puzzle.cols + ' - timeout: ' + \(timeoutMs / 1000\) + 's ---'\);
    console.log\('  Numbers: ' + JSON.stringify\(puzzle.numbers\)\);
    console.log\('  Walls: ' + JSON.stringify\(puzzle.walls\)\);

    var result = solvePuzzle\(puzzle, timeoutMs\);

    if \(result.solved\) {
        console.log\('  >> SOLVABLE'\);
        console.log\('  Solution path: [' + result.path.join\(', '\) + ']'\);

        // Verify the path
        var r = puzzle.rows, c = puzzle.cols;
        var tc = r * c;
        var ws = new Set\(\);
        for \(var wi = 0; wi < puzzle.walls.length; wi++\) {
            var w = puzzle.walls[wi];
