            var wb = w[2] * c + w[3];
            ws.add\(wa + '-' + wb\);
            ws.add\(wb + '-' + wa\);
        }
        var valid = true;
        var visitSet = new Set\(result.path\);
        if \(visitSet.size !== tc\) {
            console.log\('  VERIFY FAIL: path visits ' + visitSet.size + ' cells, expected ' + tc\);
            valid = false;
        }
        for \(var si = 1; si < result.path.length; si++\) {
            var a = result.path[si - 1], b = result.path[si];
            var ar2 = Math.floor\(a / c\), ac2 = a % c;
            var br2 = Math.floor\(b / c\), bc2 = b % c;
            var dist = Math.abs\(ar2 - br2\) + Math.abs\(ac2 - bc2\);
            if \(dist !== 1\) {
                console.log\('  VERIFY FAIL: step ' + si + ' from ' + a + ' to ' + b + ' is not orthogonal adjacent'\);
                valid = false;
                break;
            }
            if \(ws.has\(a + '-' + b\)\) {
                console.log\('  VERIFY FAIL: step ' + si + ' from ' + a + ' to ' + b + ' crosses a wall'\);
