        // Check checkpoint order
        var ne = Object.entries\(puzzle.numbers\).map\(function\(e\) { return [parseInt\(e[0]\), e[1]]; }\);
        ne.sort\(function\(a, b\) { return a[1] - b[1]; }\);
        var cpCells = ne.map\(function\(e\) { return e[0]; }\);
        var lastPos = -1;
        for \(var ci = 0; ci < cpCells.length; ci++\) {
